import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Iphone() {
  const [Products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:2001/iphones")
    // to convert json data to object form 
      .then((response) => response.json()) 
      .then((data) => {
        // to update the data
        setProducts(data.Products); 
      });
  }, []);

  return (
    <div>
      <section className="internal-page-wrapper">
        <div className="container">
          <div className="row h-100 align-items-center justify-content-center text-center">
            <div className="col-12 mt-5 pt-5">
              <h1 className="font-weight-bold">Iphone Page</h1>
              <h3>The best for the brightest.</h3>

              {iphoneProduct.map((data, index) => {
                // console.log(data)

                let productDiv = (
                  <div
                    key={data.product_url}
                    className="row justify-cintent-center product-holder h-100"
                  >
                    <div
                      className={`col-sm-12 col-md-6 my-auto order-${
                        index % 2 === 0 ? "2" : "1"
                      }`}
                    >
                      <div className="product-title">{data.product_url}</div>
                      <div className="product-brief">
                        {data.product_brief_description}
                      </div>
                      <div className="starting-price">
                        {`Starting at ${data.starting_price}`}
                      </div>
                      <div className="monthly-price">{data.price_range}</div>
                      <div className="links-wrapper">
                        <ul>
                          <li>
                            <Link to={`/iphone/${data.product_url}`}>
                              Learn More
                            </Link>
                          </li>
                        </ul>
                      </div>
                    </div>
                    <div
                      className={`col-sm-12 col-md-6 order-${
                        index % 2 === 0 ? "1" : "2"
                      }`}
                    >
                      <div className="product-image">
                        <img src={data.product_img} alt="product" />
                      </div>
                    </div>
                  </div>
                );
                return productDiv;
              })}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Iphone;
