import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Iphone() {
  const [Products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:2001/iphones")
      .then((response) => response.json())  
      .then((data) => {
        setProducts(data.Products); 
      });

  }, []);
// console.log(Products);
  let flip =true;
return (
    <>
      <section className="internal-page-wrapper">
        <div className="container">
          <div className="row h-100 align-items-center justify-content-center text-center">
            <div className="col-12 mt-5 pt-5">
              <h1 className="font-weight-bold">Iphone Page</h1>
              <div>The best for the brightest.

              </div>
              </div>
              </div>


              {Products?.map((product) => {
                let order1 = 1;
                let order2 = 2;
                if (flip){
                  order1 = 2;
                  order2 = 1;
                  flip = !flip;
                } else {
                  flip = !flip
                }

                let productDiv = (

                  <div key={Product.product_url}
                    className="row justify-cintent-center product-holder h-100">
                    <div className={`col-sm-12 col-md-6 my-auto order-${order1}`}>
                      <div className="product-title">{Product.product_name}
                      </div>
                      <div className="product-brief">
                        {Product.product_brief_description}
                      </div>
                      <div className="starting-price">
                        {`Starting at ${Product.starting_price}`}
                      </div>
                      <div className="monthly-price">{Product.price_range}</div>
                      <div className="links-wrapper">
                        <ul>
                          <li>
                            <Link to={`/iphone/${Product.product_url}`}>
                              Learn More
                            </Link>
                          </li>
                        </ul>
                      </div>
                    </div>
                    <div className={`col-sm-12 col-md-6 order-${order2 }`}
                    >
                     <div></div>
                        <img src={Product.product_img} alt="product" />

                    </div>
                  </div>
                );
                return productDiv;
              })}
            </div>
            </section>

            </>
  );
}

export default Iphone;
